// frontend/src/homepage.js
import React, { useState, useEffect } from 'react';
import { Box, Typography, Grid, Button, Card, CardContent, CardMedia } from '@mui/material';

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Оставляем один fetch запрос
    fetch('http://localhost:8000/api/products/')
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error('Ошибка загрузки товаров:', error));
  }, []);

  const handleAddToCart = async (productId) => {
    const token = localStorage.getItem('access_token');
    try {
      const response = await fetch('http://localhost:8000/api/cart/add-to-cart/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ product_id: productId, quantity: 1 }),
      });
      const result = await response.json();
      if (response.ok) {
        setCart((prevCart) => [...prevCart, result]);
      } else {
        console.error(result.error);
      }
    } catch (error) {
      console.error("Ошибка при добавлении товара в корзину:", error);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
      }}
    >
      <Box
        component="main"
        sx={{
          flex: 1,
          textAlign: 'center',
          px: 2,
          py: 2,
        }}
      >
        <Typography variant="h4" gutterBottom>
          Добро пожаловать в наш книжный магазин
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Найдите свою следующую любимую книгу!
        </Typography>

        <Grid container spacing={2} justifyContent="center" sx={{ mt: 1 }}>
          {products.map((product) => (
            <Grid item key={product.id} xs={12} sm={6} md={4}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <CardMedia
                  component="img"
                  image={product.image || '/placeholder.png'} // Замените на изображение-заглушку, если `product.image` отсутствует
                  alt={product.name}
                  sx={{
                    height: 180,
                    width: 'auto',
                    maxWidth: '100%',
                    backgroundColor: '#f5f5f5',
                  }}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    {product.name}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    {product.description}
                  </Typography>
                  <Typography variant="h6" color="primary" sx={{ mt: 1 }}>
                    {`Цена: ${product.price} BYN`}
                  </Typography>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleAddToCart(product.id)}
                    sx={{ mt: 2 }}
                  >
                    Добавить в корзину
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      <Box
        component="footer"
        sx={{
          backgroundColor: '#1976d2',
          color: 'white',
          textAlign: 'center',
          py: 1,
        }}
      >
        <Typography variant="body1">© 2025 Книжный магазин. Все права защищены.</Typography>
      </Box>
    </Box>
  );
};

export default HomePage;







