// frontend/src/CartPage.js
import React from 'react';
import { Box, Typography, List, ListItem, Button } from '@mui/material';

const CartPage = ({ token }) => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/cart/', {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        const result = await response.json();
        if (response.ok) {
          setCart(result);
        } else {
          console.error('Failed to fetch cart:', result.error);
        }
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };

    if (token) {
      fetchCart();
    }
  }, [token]);

  return (
    <Box sx={{ textAlign: 'center', mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Ваша корзина
      </Typography>
      {cart.length === 0 ? (
        <Typography variant="h6">Корзина пуста</Typography>
      ) : (
        <List>
          {cart.map((item) => (
            <ListItem key={item.id}>
              <Typography variant="body1">{item.product.name}</Typography>
              <Typography variant="body2" color="textSecondary">{`₽${item.product.price} x ${item.quantity}`}</Typography>
            </ListItem>
          ))}
        </List>
      )}
      <Button variant="contained" color="primary" sx={{ mt: 3 }}>
        Оформить заказ
      </Button>
    </Box>
  );
};

export default CartPage;



