// frontend/src/AddProductForm.js - компонент для добавления товара
import React, { useState } from 'react';
import axios from 'axios';

const AddProductForm = () => {
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productDescription, setProductDescription] = useState('');

  const handleNameChange = (e) => setProductName(e.target.value);
  const handlePriceChange = (e) => setProductPrice(e.target.value);
  const handleDescriptionChange = (e) => setProductDescription(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newProduct = {
      name: productName,
      price: productPrice,
      description: productDescription,
    };

    try {
      const response = await fetch('http://localhost:8000/api/products/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newProduct),
      });

      if (response.ok) {
        const errorData = await response.json();
        
        alert('Товар добавлен!');
      } else {
        alert('Ошибка при добавлении товара');
      }
    } catch (error) {
      alert('Ошибка сети');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Название товара:</label>
        <input
          type="text"
          value={productName}
          onChange={handleNameChange}
          required
        />
      </div>
      <div>
        <label>Цена товара:</label>
        <input
          type="number"
          value={productPrice}
          onChange={handlePriceChange}
          required
        />
      </div>
      <div>
        <label>Описание товара:</label>
        <textarea
          value={productDescription}
          onChange={handleDescriptionChange}
          required
        />
      </div>
      <button type="submit">Добавить товар</button>
    </form>
  );
};

export default AddProductForm;




